create table Accounts (username varchar(50) primary key, email varchar(50), name varchar(50), password varchar(300), phonenumber varchar(15), image longblob, imageExtension varchar(20), user_interests varchar(5000));
create table ResearchIdea (dateOfCreation datetime, advisor_username varchar(50), foreign key (advisor_username) references Accounts(username), research_name varchar(750) primary key, description text, interests text, research_image longblob, research_imageExtension varchar(20));
